import React from "react";
import { toast } from "react-toastify";

import {
  collection,
  getDocs,
  getDoc,
  addDoc,
  updateDoc,
  DeleteDoc,
  doc,
} from "firebase/firestore";
import { db } from "../firebase-config";
import Card from "./Card";
import Form from "./Form";
function Home() {
  const [tasks, setTasks] = React.useState([]);

  React.useEffect(() => {
    getTodos();
  }, []);

  React.useEffect(() => {
    console.log(tasks);
  }, [tasks]);

  function getTodos() {
    const tasksCollectionRef = collection(db, "tasks");
    getDocs(tasksCollectionRef)
      .then((res) => {
        const tasksData = res.docs.map((task) => ({
          data: task.data(),
          id: task.id,
        }));

        setTasks(tasksData);
      })
      .catch((err) => console.log(err));
  }

  React.useEffect(() => {
    console.log("im inside useEffect");
  }, []);

  return (
    <div className="container my-12 mx-auto px-4 md:px-12">
      <h1 className="text-center text-2xl italic font-medium text-blue-900 dark:text-black">
        Home page
      </h1>
      <button onClick={() => toast("hi")}>click</button>
      <br />
      <br />
      <br />

      <Form />
      {/* Cards */}
      <Card tasks={tasks} />
    </div>
  );
}
export default Home;
